import { useState, useEffect } from "react";

function TipPercent({ perc, active, onClick, customPerc, appActive }) {
  useEffect(() => {
    if (!appActive) {
      active = false;
    }
  }, [appActive]);
  return (
    <>
      <div
        onClick={onClick}
        className={`percent-btn ${
          active && !customPerc ? "active-perc" : null
        }`}
      >
        {perc}%
      </div>
    </>
  );
}

export default TipPercent;
