import InputField from "./InputField";
import TipBoard from "./TipBoard";

import dolarImg from "./images/icon-dollar.svg";
import personImg from "./images/icon-person.svg";
import { useState, useEffect } from "react";

const percentList = [5, 10, 15, 25, 50];

function Input({
  billValue,
  setBillValue,
  personValue,
  setPersonValue,
  tipAmount,
  setTipAmount,
}) {
  const [percentNum, setPercentNum] = useState("");

  const handleClick = (number) => {
    const tip =
      (Number(billValue) * Number(number)) / 100 / Number(personValue);
    if (number) {
      setPercentNum(number);
      setTipAmount(tip);
    }
  };

  return (
    <section>
      <InputField
        percentNum={percentNum}
        handleChange={handleClick}
        setTipAmount={setTipAmount}
        value={billValue}
        setValue={setBillValue}
        title="Bill"
        image={dolarImg}
      />
      <TipBoard
        handleClick={(number) => handleClick(number)}
        percentList={percentList}
      />
      <InputField
        percentNum={percentNum}
        handleChange={handleClick}
        setTipAmount={setTipAmount}
        // tipAmount={tipAmount}
        value={personValue}
        setValue={setPersonValue}
        title="Number of People"
        image={personImg}
      />
    </section>
  );
}

export default Input;
