// import { useState, useEffect } from "react";

import { useEffect } from "react";

function InputField({
  title,
  image,
  value,
  setValue,
  onSelect,
  type,
  min,
  appActive,
  setAppActive,
  initValue,
}) {
  useEffect(() => {
    if (!appActive) {
      setValue(initValue);
    }
  }, []);
  return (
    <div className="bill-box flex-col">
      <label>{title}</label>
      <input
        onSelect={onSelect}
        value={value}
        onChange={(e) => {
          setValue(e.target.value);
          setAppActive(true);
        }}
        style={{
          background: `url(${image}) no-repeat 10px 50%  #f3f9fa`,
        }}
        type={type}
        placeholder="0"
        min={min}
      />
    </div>
  );
}

export default InputField;
