import TipPercent from "./TipPercent";
import { useState, useEffect } from "react";

function TipBoard({ percentList, handleClick, percentNum }) {
  const [percentActive, setPercentActive] = useState("");

  return (
    <section className="tipboard-box">
      <p>Select Tip %</p>
      <div className="percent-box">
        {percentList.map((perc, ind) => {
          return (
            <TipPercent
              active={percentActive === perc}
              onClick={() => {
                setPercentActive(perc);
                handleClick(perc);
                setPercentNum(perc);
              }}
              key={ind}
              perc={perc}
            />
          );
        })}

        <input
          //   value={percentNum}
          //   onChange={(e) => setPercentNum(e.target.value)}
          className="percent-input"
          type="text"
          placeholder="Custom"
        />
      </div>
    </section>
  );
}

export default TipBoard;
