import InputField from "./InputField";
import TipBoard from "./TipBoard";

import dolarImg from "./images/icon-dollar.svg";
import personImg from "./images/icon-person.svg";
import { useState, useEffect } from "react";

const percentList = [5, 10, 15, 25, 50];

function Input({ billValue, setBillValue, personValue, setPersonValue }) {
  const [tipAmount, setTipAmount] = useState("");

  const handleClick = (number) => {
    const tip = (billValue * number) / 100;
  };

  return (
    <section>
      <InputField
        value={billValue}
        setValue={setBillValue}
        title="Bill"
        image={dolarImg}
      />
      <TipBoard percentList={percentList} />
      <InputField
        value={personValue}
        setValue={setPersonValue}
        title="Number of People"
        image={personImg}
      />
    </section>
  );
}

export default Input;
