import { useEffect } from "react";

function InputField({
  title,
  image,
  value,
  setValue,
  onSelect,
  type,
  min,
  setAppActive,
  isValueNull,
  cantBeZero,
}) {
  const noteStyle = cantBeZero ? { border: "2px solid red" } : null;

  return (
    <div className="bill-box flex-col">
      <label>{title}</label>
      {cantBeZero ? <span className="note">Canâ€™t be zero</span> : null}
      <input
        onSelect={onSelect}
        value={value}
        onChange={(e) => {
          setValue(e.target.value);
          setAppActive(true);
        }}
        style={{
          ...noteStyle,
          background: `url(${image}) no-repeat 10px 50%  #f3f9fa`,
        }}
        type={type}
        placeholder="0"
        min={min}
      />
    </div>
  );
}

export default InputField;
