function InputField({ title, image, value, setValue }) {
  return (
    <div className="bill-box flex-col">
      <label>{title}</label>
      <input
        value={value}
        onChange={(e) => {
          setValue(e.target.value);
        }}
        style={{
          background: `url(${image}) no-repeat 10px 50%  #f3f9fa`,
        }}
        type="text"
        placeholder="0"
        // onSelect={() => }
      />
    </div>
  );
}

export default InputField;
